-- Add data from fuel_use_scc table into point_source_scc_code
CREATE OR REPLACE FUNCTION migrate_fuel_use_data_to_point_source_scc() RETURNS void AS $$
DECLARE
	fuel_use RECORD;
BEGIN
	-- for each record of fuel_use
	FOR fuel_use IN (SELECT fus.* FROM fuel_use_scc fus) LOOP
	
				-- update point_source_scc_code record with fuel_use_scc info
				UPDATE point_source_scc_code SET 
					calculation_material_code = fuel_use.calculation_material_code,
					fuel_use_types = fuel_use.fuel_use_types
				WHERE code = fuel_use.scc_code;


		END LOOP;
 END;
 $$ LANGUAGE plpgsql;

 DO $$ 
 BEGIN
 PERFORM "migrate_fuel_use_data_to_point_source_scc"();
 DROP FUNCTION IF EXISTS migrate_fuel_use_data_to_point_source_scc();
 
 -- remove existing fuel_use_scc table
 DROP TABLE IF EXISTS fuel_use_scc;
 END;$$
 